mapper.select:
		select ID_,TYPE_,TIME_,USER_ID_,TASK_ID_,PROC_INST_ID_,ACTION_,MESSAGE_ from act_addhi_comment

		  		   and ID_ = #{id} 
		  		   and TYPE_ = #{type} 
		  		   and TIME_ = #{time} 
		  		   and USER_ID_ = #{userId} 
		  		   and TASK_ID_ = #{taskId} 
		  		   and PROC_INST_ID_ = #{procInstId} 
		  		   and ACTION_ = #{action} 
		  		   and MESSAGE_ = #{message} 



                order by ${sort} ${order}


                order by TIME_ desc



			limit #{offset}, #{limit}

select count(*) from act_addhi_comment

  		   and ID_ = #{id} 
  		   and TYPE_ = #{type} 
  		   and TIME_ = #{time} 
  		   and USER_ID_ = #{userId} 
  		   and TASK_ID_ = #{taskId} 
  		   and PROC_INST_ID_ = #{procInstId} 
  		   and ACTION_ = #{action} 
  		   and MESSAGE_ = #{message}

mapper.update:
update act_addhi_comment
TYPE_ = #{type}, TIME_ = #{time}, USER_ID_ = #{userId}, TASK_ID_ = #{taskId}, PROC_INST_ID_ = #{procInstId}, ACTION_ = #{action}, MESSAGE_ = #{message}
where ID_ = #{id}
