mapper.select:
		select t.PROC_INST_ID_,t.ID_,t.PROC_INST_ID_,b.NAME_ as processDefinitionName,e.NAME_ as contactName,b.KEY_ AS processDefinitionId,c.name as startUser,t.START_TIME_,t.END_TIME_,d.name as cntAdminDep
		FROM act_hi_taskinst t
		LEFT JOIN act_re_procdef b ON t.PROC_DEF_ID_ = b.ID_
		LEFT JOIN act_hi_procinst e ON t.PROC_INST_ID_ = e.PROC_INST_ID_
		LEFT JOIN sys_user c ON e.START_USER_ID_ = c.username
		LEFT JOIN sys_dept d ON c.dept_id = d.dept_id

		  		   and t.ID_ = #{id} 
		  		   and t.PROC_INST_ID_ = #{procInstId} 
		  		   and b.NAME_ LIKE '%'||#{processDefinitionName||'%' 
		  		   and e.NAME_ LIKE '%'||#{contactName}||'%' 
		  		   and c.name LIKE '%'||#{startUser}||'%' 
		  		   and t.ASSIGNEE_ LIKE '%'||#{assignee}||'%' 
		  		   and t.START_TIME_ >= #{startTime} 
		  		   and t.END_TIME_ <= #{endTime} 
		  		   and d.name LIKE '%'||#{cntAdminDep}||'%' 
		  		  and t.DELETE_REASON_ = 'completed'



                order by ${sort} ${order}


                order by ID_ desc



			limit #{offset}, #{limit}
