mapper.select:
		select t.ID_,t.PROC_INST_ID_,b.NAME_ as processDefinitionName,t.NAME_ as contactName,b.KEY_ AS processDefinitionId,c.name as startUser,t.START_TIME_,t.END_TIME_,d.name as cntAdminDep,f.name  AS assignee
		FROM act_hi_procinst t
		LEFT JOIN act_re_procdef b ON t.PROC_DEF_ID_ = b.ID_
		LEFT JOIN sys_user c ON t.START_USER_ID_ = c.username
		LEFT JOIN sys_dept d ON c.dept_id = d.dept_id
		LEFT JOIN act_ru_task e ON e.PROC_INST_ID_ = t.PROC_INST_ID_
		LEFT JOIN sys_user f ON e.ASSIGNEE_ = f.username

		  		   and t.ID_ = #{id} 
		  		   and t.PROC_INST_ID_ = #{procInstId} 
		  		   and b.NAME_ LIKE '%'||#{processDefinitionName}||'%' 
		  		   and t.NAME_ LIKE '%'||#{contactName}||'%' 
		  		   and t.START_USER_ID_ = #{startUser} 
		  		   and t.START_TIME_ >= #{startTime} 
		  		   and t.END_TIME_ <= #{endTime} 
		  		   and d.name LIKE ||'%',#{cntAdminDep}||'%' 



                order by ${sort} ${order}


                order by ID_ desc



			limit #{offset}, #{limit}

select count(*)
FROM act_hi_procinst t
LEFT JOIN act_re_procdef b ON t.PROC_DEF_ID_ = b.ID_
LEFT JOIN sys_user c ON t.START_USER_ID_ = c.username
LEFT JOIN sys_dept d ON c.dept_id = d.dept_id

  		   and t.ID_ = #{id} 
  		   and t.PROC_INST_ID_ = #{procInstId} 
  		   and b.NAME_ LIKE ||'%'||#{processDefinitionName||'%' 
  		   and c.name LIKE CONCAT('%',#{startUser},'%') 
  		   and t.START_TIME_ >= #{startTime} 
  		   and t.END_TIME_ <= #{endTime} 
  		   and d.name LIKE ||'%'||#{cntAdminDep}||'%'
