mapper.select:
		select id,userid,username,content,response,responder,reserve1,reserve2,reserve3,starttime,endtime from tb_advice

		  		   and id = #{id} 
		  		   and userid = #{userid} 
		  		   and username = #{username} 
		  		   and content LIKE '%'||#{content}||'%'  
		  		   and response = #{response} 
		  		   and responder = #{responder} 
		  		   and reserve1 = #{reserve1} 
		  		   and reserve2 = #{reserve2} 
		  		   and reserve3 = #{reserve3} 
		  		   and starttime = #{starttime} 
		  		   and endtime = #{endtime} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from tb_advice

  		   and id = #{id} 
  		   and userid = #{userid} 
  		   and username = #{username} 
  		   and content = #{content} 
  		   and response = #{response} 
  		   and responder = #{responder} 
  		   and reserve1 = #{reserve1} 
  		   and reserve2 = #{reserve2} 
  		   and reserve3 = #{reserve3} 
  		   and starttime = #{starttime} 
  		   and endtime = #{endtime}

mapper.update:
update tb_advice
userid = #{userid}, username = #{username}, content = #{content}, response = #{response}, responder = #{responder}, reserve1 = #{reserve1}, reserve2 = #{reserve2}, reserve3 = #{reserve3}, starttime = #{starttime}, endtime = #{endtime}
where id = #{id}
