mapper.select:
		select CNT_ID,PROC_INS_ID,CNT_CODE,CNT_NAME,CNT_TYPE,laywer_name,laywer_office,address,amount,money_amount,memo,CNT_RESERVE1,CNT_RESERVE2,CNT_RESERVE3,CNT_RESERVE4,CNT_RESERVE5 from tb_laywer_info

		  		   and CNT_ID = #{cntId} 
		  		   and PROC_INS_ID = #{procInsId} 
		  		   and CNT_CODE = #{cntCode} 
		  		   and CNT_NAME = #{cntName} 
		  		   and CNT_TYPE = #{cntType} 
		  		   and laywer_name = #{laywerName} 
		  		   and laywer_office = #{laywerOffice} 
		  		   and address = #{address} 
		  		   and amount = #{amount} 
		  		   and money_amount = #{moneyAmount} 
		  		   and memo = #{memo} 
		  		   and CNT_RESERVE1 = #{cntReserve1} 
		  		   and CNT_RESERVE2 = #{cntReserve2} 
		  		   and CNT_RESERVE3 = #{cntReserve3} 
		  		   and CNT_RESERVE4 = #{cntReserve4} 
		  		   and CNT_RESERVE5 = #{cntReserve5} 



                order by ${sort} ${order}


                order by CNT_ID desc



			limit #{offset}, #{limit}

select count(*) from tb_laywer_info

  		   and CNT_ID = #{cntId} 
  		   and PROC_INS_ID = #{procInsId} 
  		   and CNT_CODE = #{cntCode} 
  		   and CNT_NAME = #{cntName} 
  		   and CNT_TYPE = #{cntType} 
  		   and laywer_name = #{laywerName} 
  		   and laywer_office = #{laywerOffice} 
  		   and address = #{address} 
  		   and amount = #{amount} 
  		   and money_amount = #{moneyAmount} 
  		   and memo = #{memo} 
  		   and CNT_RESERVE1 = #{cntReserve1} 
  		   and CNT_RESERVE2 = #{cntReserve2} 
  		   and CNT_RESERVE3 = #{cntReserve3} 
  		   and CNT_RESERVE4 = #{cntReserve4} 
  		   and CNT_RESERVE5 = #{cntReserve5}

mapper.update:
update tb_laywer_info
PROC_INS_ID = #{procInsId}, CNT_CODE = #{cntCode}, CNT_NAME = #{cntName}, CNT_TYPE = #{cntType}, laywer_name = #{laywerName}, laywer_office = #{laywerOffice}, address = #{address}, amount = #{amount}, money_amount = #{moneyAmount}, memo = #{memo}, CNT_RESERVE1 = #{cntReserve1}, CNT_RESERVE2 = #{cntReserve2}, CNT_RESERVE3 = #{cntReserve3}, CNT_RESERVE4 = #{cntReserve4}, CNT_RESERVE5 = #{cntReserve5}
where CNT_ID = #{cntId}
