mapper.select:
		select ID,CNT_ID,PATH,NAME,TYPE,RESERVE1,RESERVE2,RESERVE3 from tb_Laywer_files

		  		   and ID = #{id} 
		  		   and CNT_ID = #{cntId} 
		  		   and PATH = #{path} 
		  		   and NAME = #{name} 
		  		   and TYPE = #{type} 
		  		   and RESERVE1 = #{reserve1} 
		  		   and RESERVE2 = #{reserve2} 
		  		   and RESERVE3 = #{reserve3} 



                order by ${sort} ${order}


                order by ID desc



			limit #{offset}, #{limit}

select count(*) from tb_Laywer_files

  		   and ID = #{id} 
  		   and CNT_ID = #{cntId} 
  		   and PATH = #{path} 
  		   and NAME = #{name} 
  		   and TYPE = #{type} 
  		   and RESERVE1 = #{reserve1} 
  		   and RESERVE2 = #{reserve2} 
  		   and RESERVE3 = #{reserve3}

mapper.update:
update tb_Laywer_files
CNT_ID = #{cntId}, PATH = #{path}, NAME = #{name}, TYPE = #{type}, RESERVE1 = #{reserve1}, RESERVE2 = #{reserve2}, RESERVE3 = #{reserve3}
where ID = #{id}
