mapper.select:
		select AID_ID,PROC_INS_ID,AID_TYPE,AID_CONTENT,AID_RESERVE1,AID_RESERVE2,AID_RESERVE3,AID_RESERVE4,AID_RESERVE5 from tb_legal_aid

		  		   and AID_ID = #{aidId} 
		  		   and PROC_INS_ID = #{procInsId} 
		  		   and AID_TYPE = #{aidType} 
		  		   and AID_CONTENT = #{aidContent} 
		  		   and AID_RESERVE1 = #{aidReserve1} 
		  		   and AID_RESERVE2 = #{aidReserve2} 
		  		   and AID_RESERVE3 = #{aidReserve3} 
		  		   and AID_RESERVE4 = #{aidReserve4} 
		  		   and AID_RESERVE5 = #{aidReserve5} 



                order by ${sort} ${order}


                order by AID_ID desc



			limit #{offset}, #{limit}

select count(*) from tb_legal_aid

  		   and AID_ID = #{aidId} 
  		   and PROC_INS_ID = #{procInsId} 
  		   and AID_TYPE = #{aidType} 
  		   and AID_CONTENT = #{aidContent} 
  		   and AID_RESERVE1 = #{aidReserve1} 
  		   and AID_RESERVE2 = #{aidReserve2} 
  		   and AID_RESERVE3 = #{aidReserve3} 
  		   and AID_RESERVE4 = #{aidReserve4} 
  		   and AID_RESERVE5 = #{aidReserve5}

mapper.update:
update tb_legal_aid
PROC_INS_ID = #{procInsId}, AID_TYPE = #{aidType}, AID_CONTENT = #{aidContent}, AID_RESERVE1 = #{aidReserve1}, AID_RESERVE2 = #{aidReserve2}, AID_RESERVE3 = #{aidReserve3}, AID_RESERVE4 = #{aidReserve4}, AID_RESERVE5 = #{aidReserve5}
where AID_ID = #{aidId}
