mapper.select:
		select CNT_ID,PATH,NAME,TYPE,UPLOADER,RESERVE1,RESERVE2,RESERVE3,REPORTTYPE,RESERVE5 from act_daily_tb

		  		   and CNT_ID = #{cntId} 
		  		   and PATH = #{path} 
		  		   and NAME LIKE '%'||#{name}||'%'  
		  		   and TYPE = #{type} 
		  		   and UPLOADER LIKE '%'||#{uploader}||'%' 
		  		   and RESERVE1 LIKE '%'||#{reserve1}||'%' 
		  		   and RESERVE2 LIKE '%'||#{reserve2}||'%' 
		  		   and RESERVE3 LIKE '%'||#{reserve3}||'%' 
		  		   and REPORTTYPE LIKE '%'||#{reportType}||'%' 
		  		   and RESERVE5 LIKE '%'||#{reserve5}||'%' 



                order by ${sort} ${order}


                order by RESERVE2 desc



			limit #{offset}, #{limit}

select count(*) from act_daily_tb

  		   and CNT_ID = #{cntId} 
  		   and PATH = #{path} 
  		   and NAME = #{name} 
  		   and TYPE = #{type} 
  		   and UPLOADER = #{uploader} 
  		   and RESERVE1 = #{reserve1} 
  		   and RESERVE2 = #{reserve2} 
  		   and RESERVE3 = #{reserve3} 
  		   and REPORTTYPE = #{reportType} 
  		   and RESERVE5 = #{reserve5}

mapper.update:
update act_daily_tb
PATH = #{path}, NAME = #{name}, TYPE = #{type}, UPLOADER = #{uploader}, RESERVE1 = #{reserve1}, RESERVE2 = #{reserve2}, RESERVE3 = #{reserve3}, REPORTTYPE = #{reportType}, RESERVE5 = #{reserve5}
where CNT_ID = #{cntId}
