mapper.select:
		select DEPT_ID_E,DEPT_TYPE,DEPT_PATH,RESERVE1,RESERVE2,RESERVE3,RESERVE4,RESERVE5 from sys_dept_extend

		  		   and DEPT_ID_E = #{deptIdE} 
		  		   and DEPT_TYPE = #{deptType} 
		  		   and DEPT_PATH = #{deptPath} 
		  		   and RESERVE1 = #{reserve1} 
		  		   and RESERVE2 = #{reserve2} 
		  		   and RESERVE3 = #{reserve3} 
		  		   and RESERVE4 = #{reserve4} 
		  		   and RESERVE5 = #{reserve5} 



                order by ${sort} ${order}


                order by DEPT_ID_E desc



			limit #{offset}, #{limit}

select count(*) from sys_dept_extend

  		   and DEPT_ID_E = #{deptIdE} 
  		   and DEPT_TYPE = #{deptType} 
  		   and DEPT_PATH = #{deptPath} 
  		   and RESERVE1 = #{reserve1} 
  		   and RESERVE2 = #{reserve2} 
  		   and RESERVE3 = #{reserve3} 
  		   and RESERVE4 = #{reserve4} 
  		   and RESERVE5 = #{reserve5}

mapper.update:
update sys_dept_extend
DEPT_TYPE = #{deptType}, DEPT_PATH = #{deptPath}, RESERVE1 = #{reserve1}, RESERVE2 = #{reserve2}, RESERVE3 = #{reserve3}, RESERVE4 = #{reserve4}, RESERVE5 = #{reserve5}
where DEPT_ID_E = #{deptIdE}
