mapper.select:
		select number,area,officename,address,phone from act_greylist_info

		  		   and number = #{number} 
		  		   and area = #{area} 
		  		   and officename = #{officename} 
		  		   and address = #{address} 
		  		  and phone =#{phone} 



                order by ${sort} ${order}


                order by number 



			limit #{offset}, #{limit}

select count(*) from act_greylist_info

  		   and number = #{number} 
  		   and area = #{area} 
  		   and officename = #{officename} 
  		   and address = #{address} 
  		  and phone =#{phone}

mapper.update:
update act_greylist_info 

	area = #{area}, 
	officename = #{officename}, 
	address = #{address},
	phone = #{phone}

where number = #{number}
