mapper.select:
		select number,area,officename,address from act_greylist

		  		   and number = #{number} 
		  		   and area = #{area} 
		  		   and officename = #{officename} 
		  		   and address = #{address} 



                order by ${sort} ${order}


                order by number 



			limit #{offset}, #{limit}

select count(*) from act_greylist

  		   and number = #{number} 
  		   and area = #{area} 
  		   and officename = #{officename} 
  		   and address = #{address}

mapper.update:
update act_greylist 

	area = #{area}, 
	officename = #{officename}, 
	address = #{address}

where number = #{number}
