mapper.select:
		select number,name,sex,age,phone,dianhua,dept,date,reserve1,reserve2 from act_lawyer_info

		  		   and number = #{number} 
		  		   and name = #{name} 
		  		   and sex = #{sex} 
		  		   and age = #{age} 
		  		   and phone = #{phone} 
		  		   and dianhua = #{dianhua} 
		  		   and dept = #{dept} 
		  		   and date = #{date} 
		  		   and reserve1 = #{reserve1} 
		  		   and reserve2 = #{reserve2} 



                order by ${sort} ${order}


                order by number desc



			limit #{offset}, #{limit}

select count(*) from act_lawyer_info

  		   and number = #{number} 
  		   and name = #{name} 
  		   and sex = #{sex} 
  		   and age = #{age} 
  		   and phone = #{phone} 
  		   and dianhua = #{dianhua} 
  		   and dept = #{dept} 
  		   and date = #{date} 
