  		   and reserve2 = #{reserve2}

mapper.update:
update act_lawyer_info
name = #{name}, sex = #{sex}, age = #{age}, phone = #{phone}, dianhua = #{dianhua}, dept = #{dept}, date = #{date}, reserve1 = #{reserve1}, reserve2 = #{reserve2}
where number = #{number}
