mapper.select:
		select ID,PATH,NAME,TYPE,UPLOADER,RESERVE1,RESERVE2,RESERVE3,RESERVE4,RESERVE5 from tb_report_doc

		  		   and ID = #{id} 
		  		   and PATH = #{path} 
		  		   and NAME LIKE '%'||#{name}||'%'  
		  		   and TYPE = #{type} 
		  		   and UPLOADER LIKE '%'||#{uploader}||'%' 
		  		   and RESERVE1 LIKE '%'||#{reserve1}||'%' 
		  		   and RESERVE2 LIKE '%'||#{reserve2}||'%' 
		  		   and RESERVE3 LIKE '%'||#{reserve3}||'%' 
		  		   and RESERVE4 LIKE '%'||#{reserve4}||'%' 
		  		   and RESERVE5 LIKE '%'||#{reserve5}||'%' 



                order by ${sort} ${order}


                order by RESERVE2 desc



			limit #{offset}, #{limit}

select count(*) from tb_report_doc

  		   and ID = #{id} 
  		   and PATH = #{path} 
  		   and NAME = #{name} 
  		   and TYPE = #{type} 
  		   and UPLOADER = #{uploader} 
  		   and RESERVE1 = #{reserve1} 
  		   and RESERVE2 = #{reserve2} 
  		   and RESERVE3 = #{reserve3} 
  		   and RESERVE4 = #{reserve4} 
  		   and RESERVE5 = #{reserve5}

mapper.update:
update tb_report_doc
PATH = #{path}, NAME = #{name}, TYPE = #{type}, UPLOADER = #{uploader}, RESERVE1 = #{reserve1}, RESERVE2 = #{reserve2}, RESERVE3 = #{reserve3}, RESERVE4 = #{reserve4}, RESERVE5 = #{reserve5}
where ID = #{id}
